return items.map((item, i) => {
  try {
    // Always classify intent from the message if there is one
    const input = (item.json.message || "").toLowerCase();

    // Always preserve bookingState and bookingData if present
    const { bookingState, bookingData } = item.json;

    // CRITICAL FIX: If we're in an active booking session, route to BookingAgent
    if (bookingState && bookingState !== "complete" && bookingState !== "") {
      return {
        json: {
          ...item.json,
          intent: "appointment",
          nextAgent: "BookingAgent",
          conversation_stage: "booking_in_progress",
          bookingState,
          bookingData
        },
        pairedItem: i
      };
    }

    // Only skip detection if there's no user message
    if (!input || input.trim() === "") {
      // Remove next_agent if present, keep everything else
      const { next_agent, ...rest } = item.json;
      return {
        json: {
          ...rest,
          bookingState,
          bookingData
          // Keep existing intent and nextAgent if no user message
        },
        pairedItem: i
      };
    }

    // Enhanced Intent detection logic with better patterns
    function detectIntent(input) {
      // Emergency detection - HIGHEST PRIORITY
      if (
        input.includes("emergency") ||
        input.includes("911") ||
        input.includes("severe pain") ||
        input.includes("can't move") ||
        input.includes("can't feel") ||
        input.includes("paralysis") ||
        input.includes("loss of consciousness") ||
        input.includes("chest pain") ||
        input.includes("difficulty breathing") ||
        input.includes("stroke") ||
        input.includes("heart attack")
      ) {
        return "escalate";
      }

      // Booking/appointment intent (PRIORITIZE booking over clinical)
      if (
        input.includes("book") ||
        input.includes("appointment") ||
        input.includes("schedule") ||
        input.includes("reschedule") ||
        input.includes("new patient") ||
        input.includes("make appointment") ||
        input.includes("set up appointment") ||
        input.includes("book appointment") ||
        input.includes("schedule appointment") ||
        input.includes("cancel appointment") ||
        input.includes("change appointment") ||
        input.includes("appointment time") ||
        input.includes("available time") ||
        input.includes("next available")
      ) {
        return "appointment";
      }

      // Clinical/symptom intent - EXPANDED PATTERNS
      if (
        input.includes("pain") ||
        input.includes("hurt") ||
        input.includes("ache") ||
        input.includes("back") ||
        input.includes("neck") ||
        input.includes("shoulder") ||
        input.includes("spine") ||
        input.includes("injury") ||
        input.includes("symptom") ||
        input.includes("discomfort") ||
        input.includes("stiffness") ||
        input.includes("soreness") ||
        input.includes("tenderness") ||
        input.includes("swelling") ||
        input.includes("inflammation") ||
        input.includes("numbness") ||
        input.includes("tingling") ||
        input.includes("weakness") ||
        input.includes("limited range") ||
        input.includes("can't bend") ||
        input.includes("can't lift") ||
        input.includes("shooting pain") ||
        input.includes("throbbing") ||
        input.includes("burning") ||
        input.includes("cramp") ||
        input.includes("spasm") ||
        input.includes("muscle") ||
        input.includes("joint") ||
        input.includes("bone") ||
        input.includes("disc") ||
        input.includes("herniated") ||
        input.includes("sciatica") ||
        input.includes("arthritis") ||
        input.includes("bursitis") ||
        input.includes("tendonitis") ||
        input.includes("fracture") ||
        input.includes("sprain") ||
        input.includes("strain") ||
        input.includes("whiplash") ||
        input.includes("slipped disc") ||
        input.includes("bulging disc") ||
        input.includes("degenerative") ||
        input.includes("hernia") ||
        input.includes("pinched nerve")
      ) {
        return "clinical";
      }

      // Billing/insurance intent - EXPANDED PATTERNS
      if (
        input.includes("cost") ||
        input.includes("price") ||
        input.includes("bill") ||
        input.includes("insurance") ||
        input.includes("coverage") ||
        input.includes("payment") ||
        input.includes("copay") ||
        input.includes("deductible") ||
        input.includes("out of pocket") ||
        input.includes("premium") ||
        input.includes("benefits") ||
        input.includes("policy") ||
        input.includes("claim") ||
        input.includes("reimbursement") ||
        input.includes("medical expenses") ||
        input.includes("treatment cost") ||
        input.includes("procedure cost") ||
        input.includes("consultation fee") ||
        input.includes("office visit") ||
        input.includes("specialist fee") ||
        input.includes("aetna") ||
        input.includes("blue cross") ||
        input.includes("cigna") ||
        input.includes("unitedhealthcare") ||
        input.includes("humana") ||
        input.includes("medicare") ||
        input.includes("medicaid")
      ) {
        return "billing";
      }

      // Feedback intent - EXPANDED PATTERNS
      if (
        input.includes("feedback") ||
        input.includes("complaint") ||
        input.includes("suggestion") ||
        input.includes("review") ||
        input.includes("rating") ||
        input.includes("experience") ||
        input.includes("satisfaction") ||
        input.includes("dissatisfied") ||
        input.includes("happy") ||
        input.includes("unhappy") ||
        input.includes("improve") ||
        input.includes("better") ||
        input.includes("worse") ||
        input.includes("recommend") ||
        input.includes("refer") ||
        input.includes("testimonial") ||
        input.includes("comment") ||
        input.includes("opinion") ||
        input.includes("thought") ||
        input.includes("feel") ||
        input.includes("like") ||
        input.includes("dislike")
      ) {
        return "feedback";
      }

      // Default to general
      return "general";
    }

    // Detect intent
    const detectedIntent = detectIntent(input);

    // Set conversation_stage to booking_start if intent is appointment and not already in booking
    let conversation_stage = item.json.conversation_stage || "";
    if (
      detectedIntent === "appointment" &&
      !conversation_stage.startsWith("booking")
    ) {
      conversation_stage = "booking_start";
    }

    // Set nextAgent based on intent with proper agent mapping
    let nextAgent = "";
    if (detectedIntent === "appointment") {
      nextAgent = "BookingAgent";
    } else if (detectedIntent === "clinical") {
      nextAgent = "MedleyAgent"; // Clinical questions go to MedleyAgent
    } else if (detectedIntent === "billing") {
      nextAgent = "BillingAgent";
    } else if (detectedIntent === "feedback") {
      nextAgent = "FeedbackAgent";
    } else if (detectedIntent === "escalate") {
      nextAgent = "EscalationAgent";
    } else {
      nextAgent = "VirtuosoAgent"; // Default agent for general questions
    }

    // Remove next_agent if present, keep everything else
    const { next_agent, ...rest } = item.json;

    // Add confidence score and additional metadata
    const confidence = calculateConfidence(input, detectedIntent);
    const urgency = detectedIntent === "escalate" ? "high" : "normal";

    // CRITICAL FIX: Preserve the original_context structure that downstream agents expect
    // Extract demo data from the current item
    const demoData = {
      id: item.json.id || item.json.demoId,
      company_name: item.json.company_name,
      agent_name: item.json.agent_name,
      industry: item.json.industry,
      category: item.json.category,
      locations: item.json.locations,
      specialists: item.json.specialists,
      tagline: item.json.tagline,
      logourl: item.json.logourl,
      team_members: item.json.team_members,
      social_accounts: item.json.social_accounts,
      contact: item.json.contact,
      google_place: item.json.google_place,
      // Preserve all other demo fields
      ...Object.keys(item.json).reduce((acc, key) => {
        if (!['message', 'intent', 'nextAgent', 'bookingState', 'bookingData', 'confidence', 'urgency', 'timestamp', 'intent_detection_method'].includes(key)) {
          acc[key] = item.json[key];
        }
        return acc;
      }, {})
    };

    // Create webhook data structure
    const webhookData = {
      message: item.json.message,
      bookingState: bookingState || "",
      bookingData: bookingData || "{}",
      userAction: item.json.userAction || "",
      timestamp: item.json.timestamp || new Date().toISOString()
    };

    // Create full context (enriched data)
    const fullContext = {
      google_place: item.json.google_place || {},
      result: item.json.result || {},
      // Preserve any other enriched data
      ...Object.keys(item.json).reduce((acc, key) => {
        if (['google_place', 'result'].includes(key)) {
          acc[key] = item.json[key];
        }
        return acc;
      }, {})
    };

    // CRITICAL FIX: Return the proper structure that downstream agents expect
    return {
      json: {
        // Chat-specific data
        message: item.json.message,
        intent: detectedIntent,
        nextAgent, // Only camelCase, no snake_case
        conversation_stage,
        bookingState,   // Always pass this forward!
        bookingData,    // Always pass this forward!
        confidence,
        urgency,
        timestamp: new Date().toISOString(),
        intent_detection_method: "rule_based",

        // CRITICAL: Preserve the original_context structure that downstream agents expect
        original_context: {
          demoData,      // All demo creation data
          webhookData,   // Chat interaction data
          fullContext    // Enriched data (Google Places, etc.)
        }
      },
      pairedItem: i
    };

  } catch (error) {
    // Error handling - return safe fallback
    console.error("Intent classifier error:", error);
    return {
      json: {
        ...item.json,
        intent: "general",
        nextAgent: "VirtuosoAgent",
        conversation_stage: item.json.conversation_stage || "",
        bookingState: item.json.bookingState,
        bookingData: item.json.bookingData,
        confidence: 0.5,
        urgency: "normal",
        error: error.message,
        timestamp: new Date().toISOString(),
        intent_detection_method: "fallback",
        // Preserve original_context even in error case
        original_context: {
          demoData: item.json,
          webhookData: {
            message: item.json.message,
            bookingState: item.json.bookingState,
            bookingData: item.json.bookingData
          },
          fullContext: {}
        }
      },
      pairedItem: i
    };
  }
});

// Helper function to calculate confidence score
function calculateConfidence(input, detectedIntent) {
  let confidence = 0.7; // Base confidence

  // Boost confidence for clear, specific patterns
  if (detectedIntent === "escalate") {
    confidence = 0.95; // High confidence for emergency
  } else if (detectedIntent === "appointment") {
    // Check for multiple appointment-related keywords
    const appointmentKeywords = ["book", "appointment", "schedule", "reschedule"];
    const keywordCount = appointmentKeywords.filter(keyword => input.includes(keyword)).length;
    confidence = Math.min(0.9, 0.7 + (keywordCount * 0.1));
  } else if (detectedIntent === "clinical") {
    // Check for multiple clinical keywords
    const clinicalKeywords = ["pain", "hurt", "ache", "back", "neck", "shoulder", "spine", "injury", "symptom"];
    const keywordCount = clinicalKeywords.filter(keyword => input.includes(keyword)).length;
    confidence = Math.min(0.85, 0.7 + (keywordCount * 0.05));
  } else if (detectedIntent === "billing") {
    // Check for multiple billing keywords
    const billingKeywords = ["cost", "price", "bill", "insurance", "coverage", "payment"];
    const keywordCount = billingKeywords.filter(keyword => input.includes(keyword)).length;
    confidence = Math.min(0.8, 0.7 + (keywordCount * 0.05));
  }

  return confidence;
}

return items.map((item, i) => {
  let locations = item.json.body?.locations;
  if (!locations) {
    // fallback: try item.json.locations (sometimes data is not nested)
    locations = item.json.locations;
  }
  if (typeof locations === "string") {
    locations = JSON.parse(locations);
  }
  if (!locations || !Array.isArray(locations) || locations.length === 0) {
    return {
      json: {
        ...item.json,
        formatted_address: "No location data"
      },
      pairedItem: i
    };
  }
  const loc = locations[0];

  const addressParts = [
    loc.address1,
    loc.address2 && loc.address2.trim() !== "" ? loc.address2 : null,
    loc.city,
    loc.state + " " + loc.zip
  ].filter(Boolean);

  return {
    json: {
      ...item.json,
      formatted_address: addressParts.join(", ")
    },
    pairedItem: i
  };
});

return items.map((item, i) => {
  // Get the main data from the Initial Processing node (Supabase row)
  const mainData = $items("Initial Processing")[0]?.json || {};
  // Get the Google Places result from the previous node (this node's input)
  const googleData = item.json?.result || {};

  // Find the message from the mainData, item.json, or userContext
  const message =
    mainData.message ||
    item.json.message ||
    (mainData.body && mainData.body.message) ||
    (item.json.body && item.json.body.message) ||
    "";

  // CRITICAL FIX: Preserve booking state from previous interactions
  // Get booking state from the current item (previous interaction response)
  const bookingState = item.json.bookingState || "";
  const bookingData = item.json.bookingData || "{}";

  // Merge all context: current item, mainData, and google_place, and always include message
  return {
    json: {
      ...item.json,         // preserves all context from previous nodes (headers, params, body, etc.)
      ...mainData,          // main data from Initial Processing
      google_place: googleData, // Google Places data
      message,              // always include message for PreludeAgent
      // CRITICAL FIX: Preserve booking state from previous interaction
      bookingState,         // <-- ADDED: Preserve booking state
      bookingData           // <-- ADDED: Preserve booking data
    },
    pairedItem: i
  };
});

// CRITICAL FIX: Process only the first item to prevent duplicates
const items = $input.all();

if (items.length === 0) {
  return [];
}

// Process only the first item
const item = items[0];

// Get the latest intent and next agent from PreludeAgent
const latestIntent = $node["PreludeAgent"].json.intent || '';
const nextAgent = $node["PreludeAgent"].json.nextAgent || '';

// Get the full, merged context from Merge Google Place Data
const fullContext = { ...$node["Merge Google Place Data"].json };

// Remove the outdated agent field if it exists
if ('agent' in fullContext) {
  delete fullContext.agent;
}

// Set or preserve conversation_stage
const conversationStage = fullContext.conversation_stage || "start";

// --- Find the user's message from all possible sources ---
let message = "";
// 1. Directly on the merged context
if (fullContext.message && fullContext.message.trim() !== "") {
  message = fullContext.message;
}
// 2. In the body object (common for webhooks)
else if (fullContext.body && fullContext.body.message) {
  message = fullContext.body.message;
}
// 3. In the original webhook node
else if ($node["Webhook"] && $node["Webhook"].json && $node["Webhook"].json.body && $node["Webhook"].json.body.message) {
  message = $node["Webhook"].json.body.message;
}
// 4. In the item itself
else if (item.json && item.json.message) {
  message = item.json.message;
}

// CRITICAL FIX: Preserve booking state from previous interactions
// Get booking state from multiple sources to ensure it's preserved
const webhookData = $items("Set Demo Context")[1]?.json || {};
const previousBookingState = fullContext.bookingState || webhookData.bookingState || "";
const previousBookingData = fullContext.bookingData || webhookData.bookingData || "{}";

// CRITICAL FIX: Check if we're in an active booking session
const isInBookingSession = previousBookingState && previousBookingState !== "" && previousBookingState !== "complete";

// Override nextAgent if in active booking session
let finalNextAgent = nextAgent;
if (isInBookingSession) {
  finalNextAgent = "BookingAgent";
}

// CRITICAL FIX: Get complete demo data from Initial Processing
const demoData = $items("Initial Processing")[0]?.json || {};

// CRITICAL FIX: Create webhook data structure for chat interactions
const webhookDataForContext = {
  message: message,
  bookingState: previousBookingState,
  bookingData: previousBookingData,
  userAction: fullContext.userAction || "",
  timestamp: new Date().toISOString()
};

// CRITICAL FIX: Create full context with enriched data
const fullContextForContext = {
  google_place: fullContext.google_place || {},
  result: fullContext.result || {},
  // Preserve any other enriched data
  ...Object.keys(fullContext).reduce((acc, key) => {
    if (['google_place', 'result'].includes(key)) {
      acc[key] = fullContext[key];
    }
    return acc;
  }, {})
};

// CRITICAL FIX: Build the unified context with ALL demo information preserved
return [{
  json: {
    ...fullContext,
    // CRITICAL FIX: Preserve ALL demo context with proper structure
    demoId: demoData.demoId || demoData.id || "bd5aa8b2-84fa-4b64-986d-7458b680b5b9",
    company_name: demoData.company_name || "Hassan Spine & Sports Medicine",
    agent_name: demoData.agent_name || "Olivia",
    industry: demoData.industry || 'healthcare',
    category: demoData.category || 'Pain Management, Sports Medicine',

    // CRITICAL FIX: Locations data - properly parsed
    locations: demoData.locations || [],
    formatted_address: demoData.formatted_address,

    // CRITICAL FIX: Contact information
    contact: demoData.contact || {
      phone: demoData.company_phone || "(732) 759-8110",
      email: demoData.company_email || "info@hassanspine.com"
    },

    // CRITICAL FIX: Practice details
    specialists: demoData.specialists || [],
    tagline: demoData.tagline,
    logourl: demoData.logourl,

    // CRITICAL FIX: Team information
    team_members: demoData.team_members || [],
    social_accounts: demoData.social_accounts || [],

    // CRITICAL FIX: Services and insurance - for BookingAgent
    services: demoData.services || [],
    insurance_providers: demoData.insurance_providers || [],

    // Google Places enriched data
    google_place: fullContext.google_place || {},

    // Intent and routing
    intent: latestIntent,
    nextAgent: finalNextAgent,
    conversation_stage: conversationStage,
    message,

    // CRITICAL FIX: Preserve booking state
    bookingState: previousBookingState,
    bookingData: previousBookingData,

    // CRITICAL FIX: Create the proper original_context structure that BookingAgent expects
    original_context: {
      demoData: {
        // ALL Dr. Hassan's business data from the 10-step form
        id: demoData.demoId || demoData.id,
        company_name: demoData.company_name,
        agent_name: demoData.agent_name,
        industry: demoData.industry,
        category: demoData.category,
        locations: demoData.locations,
        specialists: demoData.specialists,
        team_members: demoData.team_members,
        social_accounts: demoData.social_accounts,
        services: demoData.services,
        insurance_providers: demoData.insurance_providers,
        contact: demoData.contact,
        tagline: demoData.tagline,
        logourl: demoData.logourl,
        // Preserve all other demo fields
        ...Object.keys(demoData).reduce((acc, key) => {
          if (!['message', 'intent', 'nextAgent', 'bookingState', 'bookingData', 'confidence', 'urgency', 'timestamp', 'intent_detection_method'].includes(key)) {
            acc[key] = demoData[key];
          }
          return acc;
        }, {})
      },
      webhookData: webhookDataForContext,
      fullContext: fullContextForContext
    }
  },
  pairedItem: 0
}];

