// CRITICAL FIX: Enhanced Build Prompt for MedleyAgent with complete business context

// 1. Robust user message extraction (try all possible locations)
let userMessage = "";

// Try all possible locations for the user message
if ($json.message && typeof $json.message === "string" && $json.message.trim() !== "") {
  userMessage = $json.message.trim();
} else if ($json.body && typeof $json.body.message === "string" && $json.body.message.trim() !== "") {
  userMessage = $json.body.message.trim();
} else if ($json.user_message && typeof $json.user_message === "string" && $json.user_message.trim() !== "") {
  userMessage = $json.user_message.trim();
} else if ($json.userMessage && typeof $json.userMessage === "string" && $json.userMessage.trim() !== "") {
  userMessage = $json.userMessage.trim();
} else if ($json.input && typeof $json.input.message === "string" && $json.input.message.trim() !== "") {
  userMessage = $json.input.message.trim();
}

// If still empty, set a debug message
if (!userMessage) {
  userMessage = "[NO USER MESSAGE FOUND]";
}

// CRITICAL FIX: Extract complete business context from original_context structure
const demoData = $json.original_context?.demoData || $json;
const webhookData = $json.original_context?.webhookData || {};
const fullContext = $json.original_context?.fullContext || {};

// CRITICAL FIX: Extract enhanced context with fallbacks
const industry = demoData.industry || $json.industry || "healthcare";
const companyName = demoData.company_name || $json.company_name || "Hassan Spine & Sports Medicine";
const agentName = demoData.agent_name || $json.agent_name || "Olivia";
const category = demoData.category || $json.category || "Pain Management, Sports Medicine";

// CRITICAL FIX: Extract services and specialists for clinical context
const services = demoData.services || [
  'Spine Surgery', 'Non-Surgical', 'Podiatry', 'Orthopedics', 'Spinal Conditions',
  'Knee Injury', 'Shoulder Injury', 'Arthritis', 'Joint Pain', 'PRP Therapy',
  'Radiofrequency Ablation', 'Spinal Cord Stimulation', 'Pain Relief', 'Sports Injury', 'Sports Medicine'
];

const specialists = demoData.specialists || [];
const locations = demoData.locations || [];
const contact = demoData.contact || {
  phone: demoData.company_phone || "(732) 759-8110",
  email: demoData.company_email || "info@hassanspine.com"
};

// 3. Enhanced symptom extraction with Dr. Hassan's specialties
const BODY_PARTS = [
  'back', 'neck', 'shoulder', 'knee', 'hip', 'foot', 'ankle', 'wrist', 'elbow', 'leg', 'arm', 'hand', 'spine', 'joint', 'muscle', 'head', 'headache',
  'spine', 'spinal', 'lumbar', 'cervical', 'thoracic', 'disc', 'herniated', 'bulging', 'sciatica'
];
const SYMPTOMS = [
  'pain', 'ache', 'soreness', 'stiffness', 'discomfort', 'tightness', 'injury', 'hurt', 'numbness', 'weakness', 'tingling', 'swelling', 'cramp', 'spasm', 'burning', 'throbbing', 'tenderness', 'fatigue', 'trouble', 'bothering', 'uncomfortable', 'symptom', 'issue', 'problem',
  'radiating', 'shooting', 'sharp', 'dull', 'chronic', 'acute', 'intermittent', 'constant'
];

function extractSymptomsAndBodyParts(input) {
  const lower = input.toLowerCase();
  const foundParts = BODY_PARTS.filter(part => lower.includes(part));
  const foundSymptoms = SYMPTOMS.filter(sym => lower.includes(sym));
  let phrase = '';
  if (foundParts.length > 0 && foundSymptoms.length > 0) {
    phrase = `${foundParts.join(' and ')} ${foundSymptoms[0]}`;
  } else if (foundParts.length > 0) {
    phrase = foundParts.join(' and ');
  } else if (foundSymptoms.length > 0) {
    phrase = foundSymptoms[0];
  }
  return phrase || '';
}

const extractedSymptoms = extractSymptomsAndBodyParts(userMessage);

// CRITICAL FIX: Build comprehensive clinical context
const clinicalContext = `
Practice Information:
- Practice: ${companyName}
- Specialties: ${category}
- Services: ${services.join(', ')}
- Contact: ${contact.phone} | ${contact.email}
- Locations: ${locations.map(loc => loc.city).join(', ')}
- Specialists: ${specialists.map(spec => spec.name).join(', ')}
`;

// 4. Build the enhanced LLM prompt with complete business context
const prompt = `
You are ${agentName}, a compassionate healthcare assistant at ${companyName} specializing in ${category}.

${clinicalContext}

A patient says: "${userMessage}"

IMPORTANT INSTRUCTIONS:
1. Respond as a real human healthcare assistant - never mention being AI or automated
2. Show genuine empathy and understanding for the patient's situation
3. Use natural, conversational language
4. Provide specific, actionable information based on our specialties
5. Acknowledge their concern with real compassion
6. Offer relevant next steps and treatment options from our services
7. Be warm, professional, and genuinely helpful
8. Address the specific symptoms they mentioned: "${extractedSymptoms}"
9. Reference our specific services and specialists when relevant
10. Encourage them to book an appointment if appropriate

Respond with empathy, kindness, and specific reference to the patient's concern. Offer support and next steps.
`;

const system_prompt = `IMPORTANT: The user specifically mentioned: "${extractedSymptoms}". Address this in your response. You are ${agentName} at ${companyName} specializing in ${category}.`;

console.log('ðŸ”§ DEBUG - MedleyAgent Build Prompt:', {
  userMessage,
  companyName,
  agentName,
  category,
  services: services.length,
  specialists: specialists.length,
  extractedSymptoms
});

// 5. Return all fields needed for the HTTP Request node, with correct item linking
return items.map((item, i) => ({
  json: {
    ...item.json,
    prompt,
    system_prompt,
    extracted_symptoms: extractedSymptoms,
    industry,
    category: category,
    max_tokens: 512,
    temperature: 0.7,
    // CRITICAL FIX: Preserve complete business context
    demoData,
    webhookData,
    fullContext
  },
  pairedItem: i // <-- This is critical for n8n item linking!
}));

// Add this as a Code node before Chat Trigger
const demoId = $json.demoId || $json.body?.demoId || $json.body?.id || "";

if (!demoId || demoId.trim() === "") {
  // Return a default demo or error
  return [{
    json: {
      ...$json,
      demoId: "bd5aa8b2-84fa-4b64-986d-7458b680b5b9", // Use your actual demo ID
      error: "No demo ID provided, using default"
    }
  }];
}

return [{
  json: {
    ...$json,
    demoId
  }
}];

// Pass through the agent_response and suggestedActions for the webhook response
return [{
  json: {
    agent_response: $json.agent_response || "No response from assistant.",
    suggestedActions: $json.suggestedActions || []
  },
  pairedItem: 0
}];

return [{
  json: {
    agent_response: $json.agent_response,
    suggestedActions: $json.suggestedActions
  }
}];

// FIXED BookingAgent Node: Dynamic, Real-world Healthcare Booking Flow with COMPREHENSIVE VALIDATION
// Works for ANY healthcare provider, not just Dr. Hassan
// 1. Are you a New Patient or Existing Patient? (FIRST STEP - with buttons)
// 2. What is your First Name and Last Name? (collect both in one step)
// 3. Date of Birth
// 4. Phone
// 5. Email
// 6. Which location is more convenient for you? (Show dynamic locations as quick answer buttons)
// 7. Pain Level (Show buttons from 1 to 10)
// 8. Symptoms (free text)
// 9. Procedure they are interested in (show as buttons from provider's services)
// 10. What insurance do you have? (show dynamic providers as buttons)
// 11. For insurance, ask additional questions from the Insurance Verification form
// 12. Any additional info the patient would like to provide? (free text)

const items = $input.all();

// FIXED: Process only the first item to prevent duplicates
const combinedItem = items[0];

// FIXED: Extract data from the new original_context structure
const demoData = combinedItem.json.original_context?.demoData || combinedItem.json;
const webhookData = combinedItem.json.original_context?.webhookData || {};
const fullContext = combinedItem.json.original_context?.fullContext || {};

// Extract key information - DYNAMIC FOR ANY PROVIDER
const demoId = demoData.id || demoData.demoId;

// Extract company and agent info from data - DYNAMIC FOR ANY PROVIDER
let companyName = demoData.company_name || demoData.companyName || "our practice";
let agentName = demoData.agent_name || demoData.agentName || "your assistant";

// Extract locations from demo data - DYNAMIC
const locations = demoData.locations || [];
const locationNames = locations.map(loc => loc.city).filter(Boolean);

// CRITICAL FIX: Get services and insurance from the enhanced data structure
let serviceNames = [];
let insuranceProviders = [];

// CRITICAL FIX: Get services from the demo data (from Initial Processing)
if (demoData && demoData.services && Array.isArray(demoData.services)) {
  serviceNames = demoData.services;
  console.log("ðŸ”§ DEBUG - Using services from demo data:", serviceNames);
} else {
  // Fallback to hardcoded services for healthcare industry
  serviceNames = [
    "Spine Surgery",
    "Non-Surgical",
    "Podiatry", 
    "Orthopedics",
    "Spinal Conditions",
    "Knee Injury",
    "Shoulder Injury",
    "Arthritis",
    "Joint Pain",
    "PRP Therapy",
    "Radiofrequency Ablation",
    "Spinal Cord Stimulation",
    "Pain Relief",
    "Sports Injury",
    "Sports Medicine"
  ];
  console.log("ðŸ”§ DEBUG - Using fallback healthcare services:", serviceNames);
}

// CRITICAL FIX: Get insurance providers from the demo data
if (demoData && demoData.insurance_providers && Array.isArray(demoData.insurance_providers)) {
  insuranceProviders = demoData.insurance_providers;
  console.log("ðŸ”§ DEBUG - Using insurance providers from demo data:", insuranceProviders);
} else {
  // Fallback to common insurance providers
  insuranceProviders = [
    "Aetna",
    "Blue Cross Blue Shield", 
    "Cigna",
    "UnitedHealth",
    "Humana",
    "Kaiser Permanente",
    "Anthem",
    "Molina Healthcare"
  ];
  console.log("ðŸ”§ DEBUG - Using fallback insurance providers:", insuranceProviders);
}

console.log("ðŸ”§ DEBUG - Demo data:", demoData);
console.log("ðŸ”§ DEBUG - Webhook data:", webhookData);
console.log("ðŸ”§ DEBUG - Using company name:", companyName);
console.log("ðŸ”§ DEBUG - Using agent name:", agentName);
console.log("ðŸ”§ DEBUG - Available locations:", locationNames);
console.log("ðŸ”§ DEBUG - Available services:", serviceNames);

// COMPREHENSIVE VALIDATION FUNCTIONS
const validationUtils = {
  // Validate patient type (new/existing)
  validatePatientType: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please specify if you're a new or existing patient." };
    const inputLower = input.toLowerCase();
    if (inputLower.includes('new') || inputLower.includes('new_patient')) {
      return { isValid: true, value: 'new' };
    } else if (inputLower.includes('existing') || inputLower.includes('existing_patient')) {
      return { isValid: true, value: 'existing' };
    }
    return { isValid: false, error: "Please specify if you're a new or existing patient." };
  },

  // Validate full name (first and last name) with auto-correction
  validateFullName: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please provide your first and last name." };
    const trimmed = input.trim();
    if (trimmed.length < 2) return { isValid: false, error: "Please provide your first and last name." };

    const nameParts = trimmed.split(' ').filter(part => part.length > 0);
    if (nameParts.length < 2) return { isValid: false, error: "Please provide both your first and last name." };

    // Check for valid characters (letters, spaces, hyphens, apostrophes)
    const nameRegex = /^[a-zA-Z\s\-']+$/;
    if (!nameRegex.test(trimmed)) return { isValid: false, error: "Please provide a valid name using only letters, spaces, hyphens, and apostrophes." };

    // Auto-correct name formatting (capitalize first letter of each part)
    const correctedName = nameParts.map(part => 
      part.charAt(0).toUpperCase() + part.slice(1).toLowerCase()
    ).join(' ');

    return { isValid: true, value: correctedName };
  },

  // Validate date of birth
  validateDateOfBirth: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please provide your date of birth." };
    const trimmed = input.trim();

    // Check multiple date formats
    const dateFormats = [
      /^\d{1,2}\/\d{1,2}\/\d{4}$/, // MM/DD/YYYY
      /^\d{1,2}-\d{1,2}-\d{4}$/,   // MM-DD-YYYY
      /^\d{4}-\d{1,2}-\d{1,2}$/,   // YYYY-MM-DD
      /^\d{8}$/                     // MMDDYYYY
    ];

    let isValidFormat = false;
    for (const format of dateFormats) {
      if (format.test(trimmed)) {
        isValidFormat = true;
        break;
      }
    }

    if (!isValidFormat) return { isValid: false, error: "Please provide your date of birth in a valid format (e.g., MM/DD/YYYY)." };

    // Basic age validation (must be reasonable)
    const currentYear = new Date().getFullYear();
    const yearMatch = trimmed.match(/\d{4}/);
    if (yearMatch) {
      const birthYear = parseInt(yearMatch[0]);
      if (birthYear < 1900 || birthYear > currentYear) {
        return { isValid: false, error: "Please provide a valid date of birth." };
      }
    }

    return { isValid: true, value: trimmed };
  },

  // Validate phone number
  validatePhone: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please provide your phone number." };
    const trimmed = input.trim();

    // Remove all non-digit characters for validation
    const digitsOnly = trimmed.replace(/\D/g, '');

    if (digitsOnly.length < 10) return { isValid: false, error: "Please provide a valid phone number with at least 10 digits." };
    if (digitsOnly.length > 15) return { isValid: false, error: "Please provide a valid phone number." };

    // Check for reasonable phone number patterns
    const phoneRegex = /^[\+]?[1-9][\d]{0,15}$/;
    if (!phoneRegex.test(digitsOnly)) return { isValid: false, error: "Please provide a valid phone number." };

    return { isValid: true, value: trimmed };
  },

  // Validate email address
  validateEmail: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please provide your email address." };
    const trimmed = input.trim();

    // Basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(trimmed)) return { isValid: false, error: "Please provide a valid email address." };

    // Check for reasonable length
    if (trimmed.length > 254) return { isValid: false, error: "Email address is too long." };

    return { isValid: true, value: trimmed };
  },

  // Validate location selection
  validateLocation: (input, availableLocations) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please select a location." };
    const trimmed = input.trim();

    // If we have available locations, validate against them
    if (availableLocations && availableLocations.length > 0) {
      const locationLower = trimmed.toLowerCase();
      const matchingLocation = availableLocations.find(loc => 
        loc.toLowerCase() === locationLower || 
        loc.toLowerCase().includes(locationLower) ||
        locationLower.includes(loc.toLowerCase())
      );

      if (!matchingLocation) {
        return { isValid: false, error: `Please select from our available locations: ${availableLocations.join(', ')}` };
      }

      return { isValid: true, value: matchingLocation };
    }

    // If no available locations, just validate it's not empty
    if (trimmed.length < 2) return { isValid: false, error: "Please provide a valid location." };

    return { isValid: true, value: trimmed };
  },

  // Validate pain level
  validatePainLevel: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please provide your pain level." };
    const trimmed = input.trim();

    // Check if it's a number
    const painLevel = parseInt(trimmed);
    if (isNaN(painLevel)) return { isValid: false, error: "Please provide your pain level as a number from 1 to 10." };

    if (painLevel < 1 || painLevel > 10) return { isValid: false, error: "Please provide your pain level as a number from 1 to 10." };

    return { isValid: true, value: painLevel.toString() };
  },

  // Validate symptoms (free text)
  validateSymptoms: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please describe your symptoms." };
    const trimmed = input.trim();

    if (trimmed.length < 3) return { isValid: false, error: "Please provide a more detailed description of your symptoms." };
    if (trimmed.length > 500) return { isValid: false, error: "Please provide a shorter description of your symptoms." };

    return { isValid: true, value: trimmed };
  },

  // Validate procedure selection
  validateProcedure: (input, availableProcedures) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please select a procedure." };
    const trimmed = input.trim();

    // If we have available procedures, validate against them
    if (availableProcedures && availableProcedures.length > 0) {
      const procedureLower = trimmed.toLowerCase();
      const matchingProcedure = availableProcedures.find(proc => 
        proc.toLowerCase() === procedureLower || 
        proc.toLowerCase().includes(procedureLower) ||
        procedureLower.includes(proc.toLowerCase())
      );

      if (!matchingProcedure && !procedureLower.includes('other')) {
        return { isValid: false, error: `Please select from our available procedures: ${availableProcedures.join(', ')}` };
      }

      return { isValid: true, value: matchingProcedure || trimmed };
    }

    // If no available procedures, just validate it's not empty
    if (trimmed.length < 2) return { isValid: false, error: "Please provide a valid procedure." };

    return { isValid: true, value: trimmed };
  },

  // Validate insurance selection
  validateInsurance: (input, availableInsurance) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please provide your insurance information." };
    const trimmed = input.trim();

    // If we have available insurance providers, validate against them
    if (availableInsurance && availableInsurance.length > 0) {
      const insuranceLower = trimmed.toLowerCase();
      const matchingInsurance = availableInsurance.find(ins => 
        ins.toLowerCase() === insuranceLower || 
        ins.toLowerCase().includes(insuranceLower) ||
        insuranceLower.includes(ins.toLowerCase())
      );

      if (!matchingInsurance && !insuranceLower.includes('other')) {
        return { isValid: false, error: `Please select from our accepted insurance providers: ${availableInsurance.join(', ')}` };
      }

      return { isValid: true, value: matchingInsurance || trimmed };
    }

    // If no available insurance providers, just validate it's not empty
    if (trimmed.length < 2) return { isValid: false, error: "Please provide valid insurance information." };

    return { isValid: true, value: trimmed };
  },

  // Validate policy holder name (FIXED: with auto-correction like patient name)
  validatePolicyHolder: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please provide the Policy Holder's First Name and Last Name." };
    const trimmed = input.trim();

    if (trimmed.length < 2) return { isValid: false, error: "Please provide the Policy Holder's First Name and Last Name." };

    const nameParts = trimmed.split(' ').filter(part => part.length > 0);
    if (nameParts.length < 2) return { isValid: false, error: "Please provide both the Policy Holder's First Name and Last Name." };

    // Check for valid characters (letters, spaces, hyphens, apostrophes)
    const nameRegex = /^[a-zA-Z\s\-']+$/;
    if (!nameRegex.test(trimmed)) return { isValid: false, error: "Please provide a valid name using only letters, spaces, hyphens, and apostrophes." };

    // Auto-correct name formatting (capitalize first letter of each part)
    const correctedName = nameParts.map(part => 
      part.charAt(0).toUpperCase() + part.slice(1).toLowerCase()
    ).join(' ');

    return { isValid: true, value: correctedName };
  },

  // Validate insurance ID
  validateInsuranceId: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please provide your insurance ID number." };
    const trimmed = input.trim();

    if (trimmed.length < 3) return { isValid: false, error: "Please provide a valid insurance ID number." };
    if (trimmed.length > 20) return { isValid: false, error: "Insurance ID number seems too long. Please check and try again." };

    return { isValid: true, value: trimmed };
  },

  // Validate group number
  validateGroupNumber: (input) => {
    if (!input || typeof input !== 'string') return { isValid: false, error: "Please provide your group number." };
    const trimmed = input.trim();

    if (trimmed.length < 2) return { isValid: false, error: "Please provide a valid group number." };
    if (trimmed.length > 15) return { isValid: false, error: "Group number seems too long. Please check and try again." };

    return { isValid: true, value: trimmed };
  },

  // Validate additional info (optional)
  validateAdditionalInfo: (input) => {
    if (!input || typeof input !== 'string') return { isValid: true, value: "" }; // Optional field
    const trimmed = input.trim();

    if (trimmed.length > 1000) return { isValid: false, error: "Additional information is too long. Please provide a shorter description." };

    return { isValid: true, value: trimmed };
  }
};

// FIXED: Get current booking state and data from multiple possible sources
let currentBookingState = combinedItem.json.bookingState || webhookData.bookingState || "";
// If bookingState is empty or undefined, start with initial state
if (!currentBookingState || currentBookingState === "") {
  currentBookingState = "initial";
}
let currentBookingData = {};

try {
  // Try to get booking data from multiple sources
  const bookingDataSource = combinedItem.json.bookingData || webhookData.bookingData || "{}";

  if (typeof bookingDataSource === 'string') {
    currentBookingData = JSON.parse(bookingDataSource);
  } else if (typeof bookingDataSource === 'object') {
    currentBookingData = bookingDataSource;
  }
} catch (error) {
  console.log("ðŸ”§ DEBUG - Error parsing booking data:", error);
  currentBookingData = {};
}

// FIXED: Get user input from multiple possible sources
const userInput = combinedItem.json.message || webhookData.message || "";
const userAction = webhookData.userAction || "";

// Determine the actual action from user input or button click
let detectedAction = userAction;
if (!detectedAction && userInput) {
  // Check if user input contains action keywords
  const inputLower = userInput.toLowerCase();
  if (inputLower.includes('new patient') || inputLower.includes('new_patient')) {
    detectedAction = 'new_patient';
  } else if (inputLower.includes('existing patient') || inputLower.includes('existing_patient')) {
    detectedAction = 'existing_patient';
  }
}

console.log("ðŸ”§ DEBUG - User input:", userInput);
