// DYNAMIC HEALTHCARE BOOKING FLOW - WORKS FOR ANY PROVIDER WITH VALIDATION
if (currentBookingState === "initial") {
  // Initial state - DYNAMIC response for any healthcare provider
  agentResponse = `Hi there! I'm ${agentName}, and I'm here to help you get the care you need at ${companyName}. I understand this might be a difficult time for you, and I want to make this process as gentle and smooth as possible. To get started, could you please let me know if you're a:`;
  suggestedActions = [
    { text: "New Patient", action: "new_patient" },
    { text: "Existing Patient", action: "existing_patient" }
  ];
  currentBookingState = 'patient_type';
  console.log("ðŸ”§ DEBUG - Starting booking flow - asking for patient type");
} else if (currentBookingState === "patient_type") {
  // Handle patient type selection with validation
  if (detectedAction === 'new_patient' || detectedAction === 'existing_patient') {
    const patientType = detectedAction === 'new_patient' ? 'new' : 'existing';
    currentBookingData.patientType = patientType;
    agentResponse = `Thank you so much for letting me know you're a ${patientType} patient. I'm here to make this as easy as possible for you. Could you please share your First Name and Last Name with me?`;
    currentBookingState = 'collecting_name';
    console.log("ðŸ”§ DEBUG - Patient type selected via button:", patientType);
  } else if (userInput && userInput.trim()) {
    // Handle text input for patient type with validation
    const validation = validationUtils.validatePatientType(userInput);
    if (validation.isValid) {
      currentBookingData.patientType = validation.value;
      agentResponse = `Thank you for letting me know you're a ${validation.value} patient. Could you please share your First Name and Last Name with me?`;
      currentBookingState = 'collecting_name';
      console.log("ðŸ”§ DEBUG - Patient type validated:", validation.value);
    } else {
      agentResponse = validation.error;
      suggestedActions = [
        { text: "New Patient", action: "new_patient" },
        { text: "Existing Patient", action: "existing_patient" }
      ];
      validationError = validation.error;
    }
  } else {
    agentResponse = `I didn't catch that. Are you a new patient or an existing patient?`;
    suggestedActions = [
      { text: "New Patient", action: "new_patient" },
      { text: "Existing Patient", action: "existing_patient" }
    ];
  }
} else if (currentBookingState === "collecting_name") {
  // User provided name - validate and move to Step 2: Date of Birth
  if (userInput && userInput.trim()) {
    const validation = validationUtils.validateFullName(userInput);
    if (validation.isValid) {
      // Split the validated name into first and last name
      const nameParts = validation.value.split(' ');
      currentBookingData.firstName = nameParts[0];
      currentBookingData.lastName = nameParts.slice(1).join(' ');
      agentResponse = `Thank you ${currentBookingData.firstName}! I have ${currentBookingData.firstName} ${currentBookingData.lastName} as your name. Could you please share your Date of Birth? (Example: 01/15/1985)`;
      currentBookingState = 'collecting_dob';
