// CRITICAL FIX: Format Booking Response Node - Handle Summary Preservation
// Handles both LLM response data and BookingAgent output
// CRITICAL FIX: Preserve summary when BookingAgent has already generated it

return items.map((item, i) => {
  console.log("ðŸ”§ DEBUG - Format Booking Response: Processing item", item.json);

  // CRITICAL FIX: Check if this is a summary response that should be preserved
  if (item.json.preserveSummary && item.json.agent_response) {
    console.log("ðŸ”§ DEBUG - Preserving summary response from BookingAgent");
    return {
      json: {
        agent_response: item.json.agent_response,
        suggestedActions: item.json.suggestedActions || [],
        bookingState: item.json.bookingState || "confirmation",
        bookingData: item.json.bookingData || "{}",
        buttonSeparator: "OR"
      },
      pairedItem: i
    };
  }

  // CRITICAL FIX: Check if this is a BookingAgent summary response
  if (item.json.agent_response && item.json.agent_response.includes('Here\'s what I have for you')) {
    console.log("ðŸ”§ DEBUG - Detected BookingAgent summary, preserving it exactly");
    return {
      json: {
        agent_response: item.json.agent_response,
        suggestedActions: item.json.suggestedActions || [
          { text: "Yes, Schedule Appointment", action: "confirm_appointment" },
          { text: "No, Let me correct something", action: "correct_info" }
        ],
        bookingState: "confirmation",
        bookingData: item.json.bookingData || "{}",
        buttonSeparator: "OR"
      },
      pairedItem: i
    };
  }

  // Check if this is LLM response data
  const isLLMResponse = item.json.text && item.json.finish_reason;

  // Check if this is BookingAgent output
  const isBookingAgentOutput = item.json.agent_response && item.json.bookingState;

  if (isLLMResponse) {
    // This is LLM response data - extract the text as agent_response
    let agent_response = item.json.text || "No response from assistant.";

    // CRITICAL FIX: Check if this is a summary response that should be preserved
    if (item.json.bookingState === 'confirmation' || agent_response.includes('Here\'s what I have for you')) {
      console.log("ðŸ”§ DEBUG - Detected summary in LLM response, preserving it");
      return {
        json: {
          agent_response: agent_response,
          suggestedActions: item.json.suggestedActions || [],
          bookingState: "confirmation",
          bookingData: item.json.bookingData || "{}",
          buttonSeparator: "OR"
        },
        pairedItem: i
      };
    }

    // Get booking state and data - PRESERVE from BookingAgent
    const bookingState = item.json.bookingState || "initial";
    let bookingData = {};

    try {
      if (item.json.bookingData && typeof item.json.bookingData === 'string') {
        bookingData = JSON.parse(item.json.bookingData);
      } else if (item.json.bookingData && typeof item.json.bookingData === 'object') {
        bookingData = item.json.bookingData;
      }
    } catch (error) {
      bookingData = {};
    }

    // Get user action
    const userAction = item.json.userAction || "";

    // CRITICAL FIX: Preserve booking state from BookingAgent - don't override it
    let updatedBookingState = bookingState;
    let updatedBookingData = { ...bookingData };

    // Only update if we don't already have a valid booking state from BookingAgent
    if (!updatedBookingState || updatedBookingState === 'initial') {
      if (userAction === 'new_patient' || userAction === 'existing_patient') {
        updatedBookingState = 'collecting_name';
        updatedBookingData.patientType = userAction === 'new_patient' ? 'new' : 'existing';
      }
    }

    // CRITICAL: Add buttons based on updated booking state
    let suggestedActions = [];

    if (updatedBookingState === "initial" || updatedBookingState === "patient_type") {
      suggestedActions = [
        { text: "New Patient", action: "new_patient" },
        { text: "Existing Patient", action: "existing_patient" }
      ];
    } else if (updatedBookingState === "collecting_location") {
      // Add location buttons
      suggestedActions = [
        { text: "Old Bridge", action: "location_old_bridge" },
        { text: "Jersey City", action: "location_jersey_city" },
        { text: "South Plainfield", action: "location_south_plainfield" }
      ];
    } else if (updatedBookingState === "collecting_pain_level") {
      // Add pain level buttons 1-10
      suggestedActions = Array.from({length: 10}, (_, i) => ({
        text: `${i + 1}`,
        action: `pain_level_${i + 1}`
      }));
    } else if (updatedBookingState === "collecting_procedure") {
      // Add procedure buttons
      suggestedActions = [
        { text: "Pain Management Consultation", action: "procedure_pain_management" },
        { text: "Spine Treatment", action: "procedure_spine_treatment" },
        { text: "Sports Medicine", action: "procedure_sports_medicine" },
        { text: "Joint Injection", action: "procedure_joint_injection" },
        { text: "Physical Therapy", action: "procedure_physical_therapy" },
        { text: "Other", action: "procedure_other" }
      ];
    } else if (updatedBookingState === "collecting_insurance") {
      // Add insurance buttons
      suggestedActions = [
        { text: "Aetna", action: "insurance_aetna" },
        { text: "Blue Cross Blue Shield", action: "insurance_bcbs" },
        { text: "Cigna", action: "insurance_cigna" },
        { text: "UnitedHealth", action: "insurance_unitedhealth" },
        { text: "Horizon Blue Cross", action: "insurance_horizon" },
        { text: "Other", action: "insurance_other" }
      ];
    }
    // Note: collecting_name, collecting_dob, collecting_phone, collecting_email, collecting_symptoms have no buttons

    return {
      json: {
        agent_response: agent_response,
        suggestedActions: suggestedActions,
        bookingState: updatedBookingState,
        bookingData: JSON.stringify(updatedBookingData),
        buttonSeparator: "OR"
      },
      pairedItem: i
    };
  } else if (isBookingAgentOutput) {
    // This is BookingAgent output - preserve it as-is
    console.log("ðŸ”§ DEBUG - Format Booking Response: Received BookingAgent output");
    console.log("ðŸ”§ DEBUG - agent_response:", item.json.agent_response);
    console.log("ðŸ”§ DEBUG - suggestedActions:", item.json.suggestedActions);
    console.log("ðŸ”§ DEBUG - bookingState:", item.json.bookingState);
    console.log("ðŸ”§ DEBUG - bookingData:", item.json.bookingData);

    return {
      json: {
        agent_response: item.json.agent_response || "No response available",
        suggestedActions: item.json.suggestedActions || [],
        bookingState: item.json.bookingState || "initial",
        bookingData: item.json.bookingData || "{}",
        buttonSeparator: "OR"
      },
      pairedItem: i
    };
  } else {
    // Unknown data format - provide fallback
    console.log("Unknown data format in Format Booking Response:", item.json);
    return {
      json: {
        agent_response: "I apologize, but there was an error processing your request. Please try again.",
        suggestedActions: [],
        bookingState: "error",
        bookingData: {},
        buttonSeparator: "OR"
      },
      pairedItem: i
    };
  }
});

// Robust Google Calendar Fix
// Only create calendar data when booking is actually completed

return items.map((item, i) => {
  console.log('=== ROBUST GOOGLE CALENDAR FIX ===');
  console.log('Booking state:', item.json.bookingState);
  console.log('User input:', item.json.message || item.json.userMessage || '');

  // Check if booking is actually completed
  const isBookingCompleted = item.json.bookingState === 'submitted' || item.json.bookingState === 'completed';
  const hasRealPatientData = item.json.bookingData && item.json.bookingData.fullName && item.json.bookingData.email;

  console.log('Is booking completed:', isBookingCompleted);
  console.log('Has real patient data:', hasRealPatientData);

  if (isBookingCompleted && hasRealPatientData) {
    console.log('âœ… Booking completed - create calendar data');

    // PART 1: Clean LLM Response - Remove Instruction Text
    let response = item.json.agent_response || item.json.text || "";

    // Remove any instruction text in brackets []
    response = response.replace(/\[.*?\]/g, '').trim();

    // Remove any remaining instruction text patterns
    response = response.replace(/\(Please provide.*?\)/g, '').trim();
    response = response.replace(/\(Show.*?\)/g, '').trim();
    response = response.replace(/\(.*?buttons.*?\)/g, '').trim();

    // Clean up any double spaces
    response = response.replace(/\s+/g, ' ').trim();

    // PART 2: Extract Date and Time (only for completed bookings)
    let selected_slot = item.json.selected_slot;
    let selected_slot_end = null;

    if (selected_slot) {
      const start = new Date(selected_slot);
      if (!isNaN(start.getTime())) {
        const end = new Date(start.getTime() + 30 * 60000); // 30 minutes later
        selected_slot_end = end.toISOString();
      }
    }

    return {
      json: {
        ...item.json,
        agent_response: response,  // Cleaned response
        selected_slot_end,        // Extracted date/time
        proceedToGoogleCalendar: true
      },
      pairedItem: i
    };
  } else {
    console.log('â­ï¸ Booking not completed - skip calendar data creation');

    // PART 1: Clean LLM Response - Remove Instruction Text
    let response = item.json.agent_response || item.json.text || "";

    // Remove any instruction text in brackets []
    response = response.replace(/\[.*?\]/g, '').trim();

    // Remove any remaining instruction text patterns
    response = response.replace(/\(Please provide.*?\)/g, '').trim();
    response = response.replace(/\(Show.*?\)/g, '').trim();
    response = response.replace(/\(.*?buttons.*?\)/g, '').trim();

    // Clean up any double spaces
    response = response.replace(/\s+/g, ' ').trim();

    // Skip calendar data creation for incomplete bookings
    return {
      json: {
        ...item.json,
        agent_response: response,  // Cleaned response
        selected_slot: null,       // No calendar data
        selected_slot_end: null,   // No calendar data
        proceedToGoogleCalendar: false
      },
      pairedItem: i
    };
  }
});

