// RhythmAgent Parameter Mapping Fix
// This maps the data to the exact parameter structure that RhythmAgent expects

return items.map((item, i) => {
  const inputData = item.json;

  // Extract the required fields
  const patientId = inputData.patientId || `patient_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  const scheduledTime = inputData.scheduledTime || new Date().toISOString();
  const channels = inputData.channels || ['sms', 'email'];

  // Extract patient information
  const patientName = inputData.patientName || 'Unknown Patient';
  const email = inputData.email || '';
  const phone = inputData.phone || '';
  const location = inputData.location || '';
  const painLevel = inputData.painLevel || '';
  const symptoms = inputData.symptoms || '';
  const procedure = inputData.procedure || '';
  const insurance = inputData.insurance || '';
  const notes = inputData.notes || '';

  // Create the message content
  const message = `Appointment Reminder for ${patientName}

Patient Details:
- Name: ${patientName}
- Phone: ${phone}
- Email: ${email}
- Location: ${location}
- Pain Level: ${painLevel}/10
- Symptoms: ${symptoms}
- Procedure: ${procedure}
- Insurance: ${insurance}
- Notes: ${notes}

Your appointment is scheduled for ${new Date(scheduledTime).toLocaleString()}.

Please contact us if you need to reschedule.`;

  // Return the data in the exact format RhythmAgent expects for its parameters
  return {
    json: {
      // RhythmAgent Parameter Mapping
      operation: 'schedule',
      patientId: patientId,
      appointmentId: inputData.calendarEventId || '',
      reminderType: 'appointment',
      scheduledTime: scheduledTime,
      notificationChannels: channels.join(','), // Convert array to comma-separated string
      priority: 'medium',
      message: message,
      repeatFrequency: 'once',
      repeatInterval: '1',
      endDate: '', // Leave empty for single reminder

      // Additional context
      agent: 'RhythmAgent',
      timestamp: new Date().toISOString(),

      // Keep original data for reference
      originalData: inputData
    },
    pairedItem: i
  };
});
