// Shared Context Storage - Universal storage for ANY demo context
const input = $input.first().json;
const operation = input.operation || 'get';
const demoId = input.demoId || input.demo_id || '';
const demoContext = input.demoContext || {};
const message = input.message || '';
const currentState = input.currentState || 'initial';
const collectedData = input.collectedData || {};
const bookingState = input.bookingState || '';
const bookingData = input.bookingData || {};

console.log('ðŸª Shared Context Storage - Operation:', operation);
console.log('ðŸª Demo ID:', demoId);

// In-memory storage (resets on N8N restart)
if (!global.demoContextStorage) {
  global.demoContextStorage = new Map();
}

if (operation === 'store') {
  // Store demo context
  if (!demoId) {
    console.log('âŒ No demo ID provided for storage');
    return [{
      "json": {
        "success": false,
        "error": "No demo ID provided",
        "timestamp": new Date().toISOString()
      },
      "pairedItem": 0
    }];
  }

  if (!demoContext.companyName) {
    console.log('âŒ No demo context provided for storage');
    return [{
      "json": {
        "success": false,
        "error": "No demo context provided",
        "timestamp": new Date().toISOString()
      },
      "pairedItem": 0
    }];
  }

  // Store the context
  global.demoContextStorage.set(demoId, {
    demoContext: demoContext,
    storedAt: new Date().toISOString(),
    lastAccessed: new Date().toISOString()
  });

  console.log('ðŸ’¾ Stored demo context for demo ID:', demoId);
  console.log('ðŸ¢ Company:', demoContext.companyName);
  console.log('ðŸ‘¤ Agent:', demoContext.agentName);

  return [{
    "json": {
      "success": true,
      "operation": "store",
      "demoId": demoId,
      "storedAt": new Date().toISOString(),
      "message": "Demo context stored successfully"
    },
    "pairedItem": 0
  }];

} else if (operation === 'get') {
  // Retrieve demo context
  if (!demoId) {
    console.log('âŒ No demo ID provided for retrieval');
    return [{
      "json": {
        "success": false,
        "error": "No demo ID provided",
        "message": message,
        "demoContext": null
      },
      "pairedItem": 0
    }];
  }

  // Try to get from shared storage first
  const storedData = global.demoContextStorage.get(demoId);

  if (storedData && storedData.demoContext) {
    // Update last accessed time
    storedData.lastAccessed = new Date().toISOString();
    global.demoContextStorage.set(demoId, storedData);

    console.log('âœ… Retrieved demo context from shared storage for demo ID:', demoId);
    console.log('ðŸ¢ Company:', storedData.demoContext.companyName);
    console.log('ðŸ‘¤ Agent:', storedData.demoContext.agentName);

    return [{
      "json": {
        "success": true,
        "operation": "get",
        "demoId": demoId,
        "message": message,
        "demoContext": storedData.demoContext,
        "currentState": currentState,
        "collectedData": collectedData,
        "bookingState": bookingState,
        "bookingData": bookingData,
        "retrievedAt": new Date().toISOString(),
        "source": "shared_storage"
      },
      "pairedItem": 0
    }];
  }

  // NO HARDCODED FALLBACKS - This should never happen in production
  // If we get here, it means the demo context was never stored
  console.log('âŒ CRITICAL ERROR: Demo context not found for demo ID:', demoId);
  console.log('âŒ This means the demo creation webhook never fired or failed');

  return [{
    "json": {
      "success": false,
      "error": "Demo context not found. Demo creation webhook may have failed.",
      "message": message,
      "demoContext": null,
      "currentState": currentState,
      "collectedData": collectedData,
      "bookingState": bookingState,
      "bookingData": bookingData,
      "timestamp": new Date().toISOString(),
      "source": "error"
    },
    "pairedItem": 0
  }];
}

// Invalid operation
console.log('âŒ Invalid operation:', operation);
return [{
  "json": {
    "success": false,
    "error": "Invalid operation. Use \"store\" or \"get\"",
    "timestamp": new Date().toISOString()
  },
  "pairedItem": 0
}];

// Complete State Manager with Demo Context
const input = $input.first().json;
const message = input.message || '';
const demoContext = input.demoContext || {};

// Get current state from input or default to initial
let currentState = input.currentState || 'initial';
let collectedData = input.collectedData || {};
let bookingState = input.bookingState || '';
let bookingData = input.bookingData || {};

// CRITICAL FIX: Don't override currentState with bookingState
// The currentState should be preserved from the conversation flow
// Only use bookingState as a fallback if currentState is missing

// Merge collectedData with bookingData if available
if (bookingData && Object.keys(bookingData).length > 0) {
  collectedData = { ...collectedData, ...bookingData };
}

console.log('ðŸ”§ Input received:', JSON.stringify(input, null, 2));
console.log('ðŸ”§ Current State:', currentState);
console.log('ðŸ”§ Message:', message);
console.log('ðŸ”§ Demo Context:', demoContext.companyName);
console.log('ðŸ”§ Collected Data:', collectedData);
console.log('ðŸ”§ Booking State:', bookingState);
console.log('ðŸ”§ Booking Data:', bookingData);

// Simple state machine with demo context
let response = '';
let nextState = currentState;
let highlightedText = '';

switch (currentState) {
  case 'initial':
    if (message.toLowerCase().includes('appointment') || message.toLowerCase().includes('book') || message.toLowerCase().includes('schedule')) {
      response = `I'd be happy to help you schedule an appointment at ${demoContext.companyName}! Are you a new patient or an existing patient?`;
      nextState = 'waiting_for_patient_type';
      highlightedText = 'new patient or an existing patient';
    } else {
      response = `Hello! I'm ${demoContext.agentName} from ${demoContext.companyName}. I'm here to help. Would you like to schedule an appointment?`;
      nextState = 'initial';
    }
    break;

  case 'waiting_for_patient_type':
    if (message.toLowerCase().includes('new')) {
      response = `Great! I'll need some information to schedule your appointment. What is your First Name and Last Name?`;
      nextState = 'waiting_for_name';
      highlightedText = 'First Name and Last Name';
    } else if (message.toLowerCase().includes('existing')) {
      response = `Welcome back! I'll need some information to schedule your appointment. What is your First Name and Last Name?`;
      nextState = 'waiting_for_name';
      highlightedText = 'First Name and Last Name';
    } else {
      response = `Please let me know if you're a new patient or an existing patient.`;
      nextState = 'waiting_for_patient_type';
    }
    break;

  case 'waiting_for_name':
    const nameMatch = message.match(/^([A-Za-z]+)\s+([A-Za-z]+)$/);
    if (nameMatch) {
      collectedData.firstName = nameMatch[1];
      collectedData.lastName = nameMatch[2];
      response = `Thank you ${collectedData.firstName}! What is your Date of Birth?`;
      nextState = 'waiting_for_dob';
      highlightedText = 'Date of Birth';
    } else {
      response = `Please provide your First Name and Last Name (e.g., John Smith).`;
      nextState = 'waiting_for_name';
    }
    break;

  case 'waiting_for_dob':
    collectedData.dateOfBirth = message;
    response = `Thank you! What is your phone number?`;
      nextState = 'waiting_for_phone';
      highlightedText = 'phone number';
    break;

  case 'waiting_for_phone':
    collectedData.phone = message;
    response = `Great! What is your email address?`;
      nextState = 'waiting_for_email';
      highlightedText = 'email address';
    break;

  case 'waiting_for_email':
    collectedData.email = message;
    response = `Perfect! Which location is more convenient for you?\n\n${demoContext.locations.map((loc, index) => `${index + 1}. ${loc}`).join('\n')}`;
      nextState = 'waiting_for_location';
      highlightedText = 'location';
    break;

  case 'waiting_for_location':
    collectedData.location = message;
    response = `Excellent! What is your pain level from 1 to 10?`;
      nextState = 'waiting_for_pain_level';
      highlightedText = 'pain level from 1 to 10';
    break;

  case 'waiting_for_pain_level':
    collectedData.painLevel = message;
    response = `Thank you. What symptoms are you experiencing?`;
      nextState = 'waiting_for_symptoms';
      highlightedText = 'symptoms';
    break;

  case 'waiting_for_symptoms':
    collectedData.symptoms = message;
    response = `What procedure are you interested in?\n\n${demoContext.services.map((service, index) => `${index + 1}. ${service}`).join('\n')}`;
      nextState = 'waiting_for_procedure';
      highlightedText = 'procedure';
    break;

  case 'waiting_for_procedure':
    collectedData.procedure = message;
    response = `What insurance do you have?\n\n${demoContext.insuranceProviders.map((provider, index) => `${index + 1}. ${provider}`).join('\n')}\n\nNote: We do not accept Medicare or Medicaid.`;
      nextState = 'waiting_for_insurance';
      highlightedText = 'insurance';
    break;

  case 'waiting_for_insurance':
    collectedData.insurance = message;
    response = `Thank you for providing all the information! I have collected:\n\n` +
               `Name: ${collectedData.firstName} ${collectedData.lastName}\n` +
               `Date of Birth: ${collectedData.dateOfBirth}\n` +
               `Phone: ${collectedData.phone}\n` +
               `Email: ${collectedData.email}\n` +
               `Location: ${collectedData.location}\n` +
               `Pain Level: ${collectedData.painLevel}\n` +
               `Symptoms: ${collectedData.symptoms}\n` +
               `Procedure: ${collectedData.procedure}\n` +
               `Insurance: ${collectedData.insurance}\n\n` +
               `Is there anything else you'd like to add?`;
      nextState = 'complete';
    break;

  default:
    response = `I'm ${demoContext.agentName} from ${demoContext.companyName}. I'm here to help you schedule an appointment. Would you like to start?`;
      nextState = 'initial';
    break;
}

console.log('ðŸ”§ Next State:', nextState);
console.log('ðŸ”§ Response:', response);
console.log('ðŸ”§ Final Collected Data:', collectedData);

return [{
  "json": {
    "message": response,
    "currentState": nextState,
    "collectedData": collectedData,
    "highlightedText": highlightedText,
    "demoContext": demoContext,
    // Also return bookingState and bookingData for compatibility
    "bookingState": nextState,
    "bookingData": collectedData,
    "timestamp": new Date().toISOString()
  },
  "pairedItem": 0
}];
